mapper.select:
SELECT articleno, title, regdate, hit, attachfilenum FROM ( SELECT rownum r, a.* FROM ( SELECT a.articleno, title, regdate, hit, count(distinct(f.attachfileno)) attachfilenum FROM article a, attachfile f WHERE a.articleno = f.articleno(+) AND boardcd = #{boardCd}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
GROUP BY a.articleno, title, regdate, hit ORDER BY articleno DESC ) a ) WHERE r BETWEEN #{start} AND #{end}

SELECT count(*) FROM article WHERE boardcd = #{boardCd}

			AND (title LIKE '%${searchWord}%' OR
			content LIKE '%${searchWord}%')

mapper.update:
UPDATE article 
SET title=#{title},content=#{content} 
WHERE articleno=#{articleNo}

mapper.select:
SELECT articleno, title, content, email, hit, regdate
FROM article
WHERE articleno = #{articleNo}
